mapper.select:
select
  user_id, username, gender, birthday, passwd_hash, tel, avatar, province, nickname, email, address, create_time, update_time
from t_user

	 and user_id = #{userId} 
	 and username = #{username} 
	 and gender = #{gender} 
	 and tel = #{tel} 
	 and email = #{email} 
	 and birthday = #{birthday} 
	 and startDate > #{startDate} 
	 and endDate <= #{endDate}
